(112, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(113, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(114, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(115, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(116, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(117, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(118, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(119, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(120, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0),
(121, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', 'info', '{}', 'pending', 'woocommerce.com', '2024-05-24 22:14:54', NULL, 0, 'plain', '', 0, 0);
